import styles from './Card.module.scss';
import clsx from 'clsx';
import { toggleClass, getFilteredCards } from '../../redux/store';
import { useDispatch, useSelector } from 'react-redux';


const Card = props => {
  const cards = useSelector(getFilteredCards)
  const dispatch = useDispatch();

  const toggle = () => {
    dispatch(toggleClass(props.id, props.isFavourite));
  };

  return (
    <li className={styles.card}>
      {props.title}
      <div>
        <button
          onClick={toggle}
          className={clsx(styles.star_button, props.isFavourite === true && styles.active)}>
          <span className='fa fa-star-o' />
        </button>

      </div>
    </li>
  );
};

export default Card;
