import PageTitle from './../PageTitle/PageTitle';
import styles from './Favourite.module.scss'
import Card from '../Card/Card';
import { useSelector } from 'react-redux';
import { toggleClass } from '../../redux/store';



const Favourite = (props) => {
  const cards = useSelector(state => toggleClass(state, props.isFavourite, props.id));
  return (
    <div className={styles.column}>
      <PageTitle>Favourite</PageTitle>
      <span className={styles.icon + ' fa fa-star-o'} />
      <h2 className={styles.title}>{props.title}</h2>
      <ul className={styles.cards}>
        {cards.map((card) => (
          <Card key={card.id} id={card.id} title={card.title} isFavourite={card.isFavourite} />))}
      </ul>

    </div>
  )
}

export default Favourite;
